import { styled } from '@mui/material/styles';
import Box from '@mui/material/Box';
export const ButtonsGroup = styled(Box)(({theme}) =>({
    display: "flex",
    justifyContent: "center",
    gap: "10px",
    [theme.breakpoints.up("md")]:{
      marginTop: "15px",
      width: "40vw",
    }
}));
