import { styled } from '@mui/material/styles';
export const GeneralPage = styled("div")(({theme, size}) =>({
  alignItems: "center",
  display: "flex",
  justifyContent:"center",
  overflowX: "visible",
  flexDirection: "column",
  flexWrap: "wrap",
  minHeight: "100vh",
  minWidth: "100vw",
  height: size ? size : "",
  [theme.breakpoints.down("md")]:{
    height: "",
  }
}))
