function ProductInfo() {
  const {id} = useParams()
  const [product, setProduct] = useState();
  const fetchSingleProduct = async() =>{
    try {
      const fetchData =await axios.get(`http://localhost:8000/products/${id}`)
      setProduct(fetchData.data)

    } catch (err) {
      console.error(err)
    }
  }
  useEffect(() => {
    fetchSingleProduct();
  }, [id]);
  return (
    <GeneralPage>
      ProductInfo

    </GeneralPage>
  )
}

